 'select' on 'change' event & a '.reuired' classed multifield on 'keyup':    $('form')        .on('blur', 'input[required], input.optional, select.required', validator.checkField)        .on('change', 'select.required', validator.checkField)        .on('keypress', 'input[required][pattern]', validator.keypress);    $('.multi.required')        .on('keyup blur', 'input', function () {            validator.checkField.apply($(this).siblings().last()[0]);        });    // bind the validation to the form submit event    //$('#send').click('submit');//.prop('disabled', true);    $('form').submit(function (e) {        e.preventDefault();        var submit = true;        // evaluate the form using generic validaing        if (!validator.checkAll($(this))) {            submit = false;        }        if (submit)            this.submit();        return false;    });    /* FOR DEMO ONLY */    $('#vfields').change(function () {        $('form').toggleClass('mode2');    }).prop('checked', false);    $('#alerts').change(function () {        validator.defaults.alerts = (this.checked) ? false : true;        if (this.checked)            $('form .alert').remove();    }).prop('checked', false);</script><script type="text/javascript">     $(document).ready(function(){        //Examples of how to assign the Colorbox event to elements        $(".group1").colorbox({rel:'group1'});    });</script><script type="text/javascript" src="<?php echo base_url()?>assets/tinymce/tinymce.min.js"></script><script type="text/javascript">$(document).ready(function() {    tinymce.init({        selector: "textarea",        theme: "modern",        paste_data_images: true,        plugins: [            "advlist autolink lists link image charmap print preview hr anchor pagebreak",            "searchreplace wordcount visualblocks visualchars code fullscreen",            "insertdatetime media nonbreaki